// Convertir Metals.dev -> formato compatible con /predict
// Salida esperada: { rates: { 'YYYY-MM-DD': { USD: <precio> }, ... } }

const payload = $json || {};
const formatted = {};

// Soportar dos posibles formas de respuesta:
// 1) rates: { 'YYYY-MM-DD': { metals: { gold: ... } } }
// 2) series: [ { date: 'YYYY-MM-DD', metals: { gold: ... } }, ... ]

if (payload.rates && typeof payload.rates === 'object') {
  for (const [date, entry] of Object.entries(payload.rates)) {
    const goldPrice =
      entry?.metals?.gold ??
      entry?.metals?.XAU ?? // por si la API usara XAU
      null;

    if (goldPrice != null && !isNaN(Number(goldPrice))) {
      formatted[date] = { USD: Number(goldPrice) };
    }
  }
} else if (Array.isArray(payload.series)) {
  for (const day of payload.series) {
    const date = day?.date;
    const goldPrice =
      day?.metals?.gold ??
      day?.metals?.XAU ??
      null;

    if (date && goldPrice != null && !isNaN(Number(goldPrice))) {
      formatted[date] = { USD: Number(goldPrice) };
    }
  }
}

// Validaci√≥n m√≠nima para evitar enviar vac√≠o
if (Object.keys(formatted).length === 0) {
  throw new Error('No se encontraron valores de oro en la respuesta de Metals.dev');
}

return [{ json: { rates: formatted } }];

// Procesar par√°metros de entrada
const body = $json || {};

// Extraer timeframe como n√∫mero (eliminar 'd' si existe)
let timeframeValue = body.timeframe || '30d';
if (typeof timeframeValue === 'string') {
  timeframeValue = parseInt(timeframeValue.replace(/[^0-9]/g, ''), 10) || 30;
}

// Extraer prediction_horizon como n√∫mero
let horizonValue = body.prediction_horizon || '2d';
if (typeof horizonValue === 'string') {
  horizonValue = parseInt(horizonValue.replace(/[^0-9]/g, ''), 10) || 2;
}

return [{
  execution_id: body.execution_id || (Date.now().toString()),
  timeframe: timeframeValue,
  investment_amount: Number(body.investment_amount || 1000),
  prediction_horizon_days: Math.min(Math.max(horizonValue, 1), 3),
  symbol: 'XAUUSD'
}];

