// Analizar resultados de la IA
const predictionResult = $input.first().json;
const goldData = $node['üß© Transform Metals.dev Data'].json; // usar datos transformados
const params = $node['‚öôÔ∏è Process Parameters'].json;

// Calcular m√©tricas adicionales
const rates = goldData.rates || {};
const dates = Object.keys(rates).sort();
const prices = dates.map(date => rates[date]?.USD || 0);

// Guardas por datos insuficientes
if (prices.length < 2) {
  throw new Error('Insufficient gold price data');
}

const currentPrice = prices[prices.length - 1] || 0;
const previousPrice = prices[prices.length - 2] || currentPrice;
const priceChange = currentPrice - previousPrice;
const priceChangePercent = previousPrice > 0 ? (priceChange / previousPrice) * 100 : 0;

// Generar recomendaci√≥n basada en predicci√≥n de IA (mantener compatibilidad)
let recommendation = predictionResult.recommendation || predictionResult.prediction || 'HOLD';
let risk_level = predictionResult.risk_level || 'MEDIUM';

// Preparar datos para gr√°fico (serie simple)
const chartData = dates.map((d, i) => ({ date: d, price: prices[i] }));

return [{
  execution_id: params.execution_id,
  timeframe: params.timeframe,
  investment_amount: params.investment_amount,
  current_price: currentPrice,
  price_change: priceChange,
  price_change_percent: priceChangePercent,
  ai_recommendation: recommendation,
  predicted_price: predictionResult.predicted_price || currentPrice,
  prediction_horizon: predictionResult.horizon || '7 days',
  // An√°lisis de riesgo
  risk_level: risk_level,
  volatility: predictionResult.volatility || 0.1,
  // Datos para gr√°fico
  chart_data: chartData,
  historical_data: goldData.rates,
  // Timestamps
  processed_at: new Date().toISOString(),
  data_points: prices.length,
  confidence: predictionResult.confidence
}];

// Manejar errores
const item = $input.first();
const err = item.json || {};
const originalParams = $node['‚öôÔ∏è Process Parameters'].json;

return [{
  error: true,
  message: err?.message || 'Error desconocido en el an√°lisis',
  execution_id: originalParams.execution_id,
  timestamp: new Date().toISOString(),
  suggestion: 'Por favor intenta nuevamente en unos minutos'
}];
